import { Chart, ScatterSeries, Legend, Tooltip, ILoadedEventArgs } from '@syncfusion/ej2-charts';
Chart.Inject(ScatterSeries, Legend, Tooltip);

/**
 * Scatter Series
 */

    let series1: Object[] = [];
    let series2: Object[] = [];
    let point1: Object;
    let value: number = 80;
    let value1: number = 70;
    let i: number;
    for (i = 1; i < 120; i++) {
        if (Math.random() > 0.5) {
            value += Math.random();
        } else {
            value -= Math.random();
        }
        value = value < 60 ? 60 : value > 90 ? 90 : value;
        point1 = { x: 120 + (i / 2), y: value.toFixed(1) };
        series1.push(point1);
    }
    for (i = 1; i < 120; i++) {
        if (Math.random() > 0.5) {
            value1 += Math.random();
        } else {
            value1 -= Math.random();
        }
        value1 = value1 < 60 ? 60 : value1 > 90 ? 90 : value1;
        point1 = { x: 120 + (i / 2), y: value1.toFixed(1) };
        series2.push(point1);
    }
    let chart: Chart = new Chart({

        //Initializing Primary X Axis
        primaryXAxis: {
            title: 'Height (cm)',
            minimum: 120,
            maximum: 180,
            edgeLabelPlacement: 'Shift',
            labelFormat: '{value}cm'
        },

        //Initializing Primary Y Axis	
        primaryYAxis:
        {
            title: 'Weight (kg)',
            minimum: 60,
            maximum: 90,
            labelFormat: '{value}kg',
            rangePadding: 'None'
        },

        //Initializing Chart Series
        series: [
            {
                type: 'Scatter',
                dataSource: series1,
                xName: 'x', width: 2, marker: {
                    visible: false,
                    width: 10,
                    height: 10
                },
                yName: 'y', name: 'Male', opacity : 0.7
            },
            {
                type: 'Scatter',
                dataSource: series2,
                xName: 'x', width: 2, marker: {
                    visible: false,
                    width: 10,
                    height: 10
                },
                yName: 'y', name: 'Female', opacity : 0.7
            },
        ],

        //Initializing Chart title
        title: 'Height Vs Weight', legendSettings: { visible: true },
        tooltip: { enable: true, format: '${series.name}<br>Height: ${point.x}<br>Weight: ${point.y}', enableAnimation : false },
        load: (args: ILoadedEventArgs) => {
            let selectedTheme: string = location.hash.split('/')[1];
            args.chart.theme = selectedTheme.indexOf('fabric') > -1 ? 'Fabric' : 'Material';
        }
    });
    chart.appendTo('#container');

