        width: '100%',
        height: '650px',
        cellTemplate: '${if(type === "monthCells")}<div class="templatewrap">${getCellContent(data.date)}</div>${/if}',
        views: ['Month'],
        currentView: 'Month',
        selectedDate: new Date(2017, 11, 15)
    });
